mapper.select:
select *
from ${tablename}


		and ID = #{id}


		and warehourseID = #{warehourseid}


		and warehourseName = #{warehoursename}


		and clientID = #{clientid}


		and clientName = #{clientname}


		and principalID = #{principalid}


		and principalName = #{principalname}


		and status = #{status}


		and type = #{type}


		and note = #{note}


		and exception = #{exception}
