mapper.select:
select *
from ${tablename}


		and ID = #{id}


		and name = #{name}


		and phone = #{phone}


		and email = #{email}


		and label = #{label}

mapper.insert:
insert into ${tablename} (ID, password, hourseID, 
  hourseName, name, gender, 
  phone, email, label
  )
values (#{id,jdbcType=VARCHAR}, #{password,jdbcType=VARCHAR}, #{hourseid,jdbcType=INTEGER}, 
  #{hoursename,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}, #{gender,jdbcType=VARCHAR}, 
  #{phone,jdbcType=VARCHAR}, #{email,jdbcType=VARCHAR}, #{label,jdbcType=VARCHAR}
  )

mapper.update:
update ${tablename}
set password = #{password,jdbcType=VARCHAR},
  hourseID = #{hourseid,jdbcType=INTEGER},
  hourseName = #{hoursename,jdbcType=VARCHAR},
  name = #{name,jdbcType=VARCHAR},
  gender = #{gender,jdbcType=VARCHAR},
  phone = #{phone,jdbcType=VARCHAR},
  email = #{email,jdbcType=VARCHAR},
  label = #{label,jdbcType=VARCHAR}
where ID = #{id,jdbcType=VARCHAR}
